import React, { useEffect, useState } from 'react'
import VideoContainer from './VideoContainer'
import { COUR_IMG1, COUR_IMG2, COUR_IMG3, COUR_IMG4 } from './constants'

const MainContainer = () => {
  const arr=[COUR_IMG1,COUR_IMG2,COUR_IMG3,COUR_IMG4]
  const[img,setImg]=useState(COUR_IMG1)
  useEffect(()=>{
    for(let i=1;i<arr.length;i++){
      setTimeout(()=>{
            setImg(arr[i])
      },2000)
    }
    
  },[i])
  return (
    <>
     <div className='ml-8 mt-2 w-[1200px] h-[200px] overflow-hidden'><img src={img} alt="" /></div>
     <div className='ml-16 flex flex-wrap'>
      <VideoContainer/>
     </div>
    </>
  )
}

export default MainContainer
