import React from 'react'
import AddToCartPage from './AddToCartPage'
import { useDispatch, useSelector } from 'react-redux'

const AddToCartPageMain = () => {
    const dispatch=useDispatch()
    const items=useSelector((store)=>store.cart.items)
    const clearCart=useSelector((store)=>store.cart.clearCart)
    const handleClearCart=async()=>{
        await dispatch(clearCart)
    }
  return (
    <>
       <div className='w-full text-blue-600 hover:underline text-right pr-11 cursor-pointer' onClick={()=>handleClearCart()}>Clear Cart</div>
    {items.map((item)=><AddToCartPage key={item.id} item={item}/>)}
    </>
  )
}

export default AddToCartPageMain
