import React from 'react'
import { useSearchParams } from 'react-router-dom'

const SearchProduct = ({ products }) => {
  const [searchParam] = useSearchParams()
  const element = searchParam.get("v")
  return (
    <>
      {/* <div className='text-center'>Search Result of "{element.toUpperCase()}"</div> */}
      <div className='flex ml-16 mt-5'>
        <img src={products.thumbnail} alt="loading" className='w-[300px] h-[145px] rounded-lg' />
        <div className='ml-2 '>
          <div className='text-xl font-bold'>{products.title}</div>
          <div>{products.description}</div>
          <div>₹{((products.price) * 81).toLocaleString()}</div>
          <div>⭐{products.rating}</div>
        </div>
      </div>
    </>


  )
}

export default SearchProduct
