            <Link to={'/search?v='}><button className='w-[100px] h-[50px]'>ðŸ”Ž</button></Link>
          <Link to='/AddToCart'><li className=' text-2xl  hover:underline w-[100px] '>Cart<sup className=' ml-1 text-white '>12</sup></li></Link>
        </ul>
      </div>
    </>
  )
}

