import React from 'react'
import Header from './Header'
import Navbar from './Navbar'
import Footer from './Footer'
import AddToCartShimmer from './AddToCartShimmer'
import { useSearchParams } from 'react-router-dom'
import { useState,useEffect } from 'react'
import AddToCartPage from './AddToCartPage'

const AddToCart = () => {
  const[searchParam]=useSearchParams()
  const[isLoading,setIsLoading]=useState(true)
  const[product,setProduct]=useState([])
  useEffect(()=>{
     getProduct()
  },[])
  const getProduct=async()=>{
    const data=await fetch(`https://dummyjson.com/products/${searchParam.get("v")}`)
    const json= await data.json()
    setProduct(json)
    setIsLoading(false)
  }
  return (
    <>
      <Header />
      <Navbar />
      {console.log(product.title)}
      {isLoading ? <AddToCartShimmer/> : product.length>0 ? product.map((product)=><AddToCartPage product={product}/>) : <div className='text-3xl font-Poppins font-bold'>This is empty</div>}
      <Footer />
    </>
  )
}

export default AddToCart
