import React, { useEffect, useState } from 'react'
import Navbar from './Navbar'
import { useSearchParams } from 'react-router-dom'
import SinglePageProductShimmer from './SinglePageProductShimmer'
import Header from './Header'
import Footer from './Footer'
import UserReviewCard from './UserReviewCard'
import { useDispatch, useSelector } from 'react-redux'
import { addToCart } from './cartSlice'
import { Link } from 'react-router-dom'

const SinglePageProduct2 = () => {
    const dispatch=useDispatch()
    const[searchParam] = useSearchParams()
    const[item,setItem]=useState({})
    const[isloading,setIsLoading]=useState(true)
    const[view,setView]=useState(false)
    useEffect(()=>{
        getSingleProduct()
    },[])
    const getSingleProduct=async()=>{
        const data = await fetch(`https://dummyjson.com/products/${searchParam.get("v")}`)
        const json = await data.json()
        setItem(json)
        setIsLoading(false)
    }

    if(isloading===true) return <SinglePageProductShimmer/>
     
  return (
   <>
    <Header/>
    <Navbar/>
    <div className='bg-gray-200 h-[500px] font-Poppins'>
        <div className='flex pt-3 ml-1 mr-1'>
            <div className='bg-white w-[200px] h-[300px] mr-2'></div>
            <div className='bg-white w-[550px] h-[300px] mr-2 text-center text-2xl font-bold'>
                <div>{item.title}</div>
                <div className='font-light'>{item.brand}</div>
                <img className='w-[300px] h-[250px] rounded-lg ml-[125px]' src={item.thumbnail} alt="loading" />
                </div>
            <div className='bg-white w-[300px] h-[300px] mr-2'></div>
            <div className='bg-white w-[200px] h-[300px]'></div>
        </div>
      
    </div>
    <Footer/>
   </>
  )
}

export default SinglePageProduct2
