import React from 'react'

const AddToCartPage = ({product}) => {

//     <div className='flex ml-16 mt-5'>
//     <div className='w-[200px] h-[100px] bg-gray-300 rounded-lg'></div>
//     <div className='ml-2'>
//       <div className='w-[700px] h-[15px] mt-2 rounded-lg bg-gray-300'></div>
//       <div className='w-[500px] h-[15px] mt-2 rounded-lg bg-gray-300'></div>
//       <div className='w-[300px] h-[15px] mt-2 rounded-lg bg-gray-300'></div>
//       <div className='w-[100px] h-[15px] mt-2 rounded-lg bg-gray-300'></div>
//     </div>
//   </div>


  return (
    <>
    <div className='flex ml-16 mt-5 font-Poppins'>
        <img src={product.thumbnail} alt="loading" className='w-[200px] h-[150px] rounded-lg' />
        <div className='ml-2'>
            <div className='text-xl font-bold mb-1'>{product.title}</div>
            <div className='font-light mb-1'>{product.description}</div>
            <div className='text-xl font-bold'>₹{((product.price) * 81).toLocaleString()}.00</div>
            <div className='font-light'>⭐{product.rating}</div>
            <div className='flex '>
                <div className='text-[12px] font-thin mr-2'>remove from cart</div>
                <div className='text-[12px] font-thin mr-3'>buy this</div>
                <div className='text-[12px] font-thin'>save for review</div>
            </div>
        </div>

    </div>
    </>
  )
}

export default AddToCartPage
