import { createSlice } from "@reduxjs/toolkit";
const cartSlice= createSlice({
    name:'cart',
    initialState:{
         items:[],
         cartQuantity:0,
         quantity:1
    },
    reducers:{
        addToCart:(state,action)=>{
            const existingIndex=state.items.findIndex((item)=>item.id=== action.payload.id)
            
        },
        removeToCart:(state,action)=>{
          const nextItems =  state.items.filter((item)=>(item.id!==action.payload.id))
          state.items=nextItems
          state.cartQuantity=state.cartQuantity-1
        },
        addProductQuantity:(state)=>{
            state.quantity=state.quantity+1
        },
        clearCart:(state)=>{
            state.items=[]
            state.cartQuantity=0
        }

    }
})

export const{addToCart,removeToCart,addProductQuantity,clearCart} = cartSlice.actions
export default cartSlice.reducer
